/**
 * @namespace
 */
namespace Kaltura\Client\Service;

/**
 * @package Kaltura
 * @subpackage Client
 */
class BookmarkService extends \Kaltura\Client\ServiceBase
{
	function __construct(\Kaltura\Client\Client $client = null)
	{
		parent::__construct($client);
	}

	/**
	 * Report player position and action for the user on the watched asset. Player position is used to later allow resume watching.
	 * 
	 * @return bool
	 */
	function add(\Kaltura\Client\Type\Bookmark $bookmark)
	{
		if ($this->client->isMultiRequest())
			throw $this->client->getClientException("Action is not supported as part of multi-request.", ClientException::ERROR_ACTION_IN_MULTIREQUEST);
		
		$kparams = array();
		$this->client->addParam($kparams, "bookmark", $bookmark->toParams());
		$this->client->queueServiceActionCall("bookmark", "add", null, $kparams);
		$resultXml = $this->client->doQueue();
		$resultXmlObject = new \SimpleXMLElement($resultXml);
		$this->client->checkIfError($resultXmlObject->result);
		$resultObject = (bool)\Kaltura\Client\ParseUtils::unmarshalSimpleType($resultXmlObject->result);
		return $resultObject;
	}

	/**
	 * Returns player position record/s for the requested asset and the requesting user. 
	 *             If default user makes the request â€“ player position records are provided for all of the users in the household.
	 *             If non-default user makes the request - player position records are provided for the requesting user and the default user of the household.
	 * 
	 * @return \Kaltura\Client\Type\BookmarkListResponse
	 */
	function listAction(\Kaltura\Client\Type\BookmarkFilter $filter)
	{
		if ($this->client->isMultiRequest())
			throw $this->client->getClientException("Action is not supported as part of multi-request.", ClientException::ERROR_ACTION_IN_MULTIREQUEST);
		
		$kparams = array();
		$this->client->addParam($kparams, "filter", $filter->toParams());
		$this->client->queueServiceActionCall("bookmark", "list", "KalturaBookmarkListResponse", $kparams);
		$resultXml = $this->client->doQueue();
		$resultXmlObject = new \SimpleXMLElement($resultXml);
		$this->client->checkIfError($resultXmlObject->result);
		$resultObject = \Kaltura\Client\ParseUtils::unmarshalObject($resultXmlObject->result, "KalturaBookmarkListResponse");
		$this->client->validateObjectType($resultObject, "\\Kaltura\\Client\\Type\\BookmarkListResponse");
		return $resultObject;
	}
}
